// Same approach but Assuming 1 at even indices and 0 at odd indices


class Solution {
public:
    int minOperations(string s) {
        int n=s.length();
        int count=0;
    
        for(int i=0;i<n;i++){
            if(i%2==0 && s[i]=='1'){
                count++;
            }
            if(i%2==1 && s[i]=='0'){
                count++;
            }
        }

       return min(count,n-count); 
    }
};